import { GetUsernameFromCoockies } from '@/assets/get_username_from_cookies.js'


class SetBankToOperator {
    constructor (event) {
        this.headers = {'Content-Type': 'application/json'}
        this.checked = event.target.checked
        this.id = event.target.id
        this.url = `http://127.0.0.1:8000/api/operator_user/define_bank/`
        this.username = new GetUsernameFromCoockies(document.cookie).findUsernameString()
        this.body = {
            'username': this.username,
            'bank': this.getBankName()
        }
    }

    getNameRegistry () {
        return this.id.split(' ')[1]
    }

    getBankName () {
        return this.getNameRegistry().split('_')[0]
    }

    setBankToOperator () {
        return fetch(this.url, {
            method: 'PUT',
            body: JSON.stringify(this.body),
            headers: this.headers
        }).then(response => {
            return response.json()
        })
    }

    isCheckButton() {
        if (this.checked == true) {
            this.setBankToOperator ()
        }
    }
}


class SetClientsModelToOperator {
    constructor (event) {
        this.headers = {'Content-Type': 'application/json'}
        this.checked = event.target.checked
        this.id = event.target.id
        this.url = `http://127.0.0.1:8000/api/operator_user/define_clients_model/`
        this.username = new GetUsernameFromCoockies(document.cookie).findUsernameString()
        this.body = {
            'username': this.username,
            'clients_model': this.getClientsName()
        }
    }

    getClientsModelName () {
        return this.id.split(' ')[1]
    }

    getClientsName () {
        return this.getClientsModelName().split('_')[0]
    }

    setClientModelToOperator () {
        document.cookie = `user_class_model=${this.getClientsName()}; secure`
        localStorage.setItem('user_class_model', this.getClientsName());
        // let model_class = localStorage.getItem('user_class_model')
        let connect = localStorage.getItem('test_connection')
        connect

        return fetch(this.url, {
                    method: 'PUT',
                    body: JSON.stringify(this.body),
                    headers: this.headers
                }).then(response => {
                    return response.json()
                })
        // let username = new GetUsernameFromCoockies(document.cookie).findUsernameString ()
        // var url = `ws://localhost:8000/ws/chat/${username}/`
        // console.log('Starting connection to WebSocket Server')
        // this.connection = new WebSocket(url)

    }

    // setClientModelToOperator () {
    //     return fetch(this.url, {
    //         method: 'PUT',
    //         body: JSON.stringify(this.body),
    //         headers: this.headers
    //     }).then(response => {
    //         return response.json()
    //     })
    // }
}

export { SetBankToOperator, SetClientsModelToOperator };
