class OnStatistic {
    constructor (event) {
        this.event = event;
        this.id = this.returnRefactorId ()
        this.url = 'http://127.0.0.1:8000/api/statistics/create_statistic_entry'
        this.body = this.createBody ()
    }

    returnRefactorId () {
        return this.event.split(' ')[1]
    }

    getActiveBank () {
        let buttonsCheckbox = document.getElementsByClassName('btn-check header-button')
        for (let bttn of buttonsCheckbox) {
            if (bttn.checked == true) {
                let bttnId = bttn.id.replace('_registry', '').split(' ')[1]
                return bttnId
            }
        }
    }

    getClassModel () {
        let classModel = localStorage.getItem('user_class_model')
        return classModel
    }

    getInn () {
        let inn = document.getElementById(`inn ${this.id}`)
        console.log(`inn ${this.id}`)
        console.log(inn)
        return inn
    }

    createBody () {
        let body =  {
            username:           'tanya',
            inn: this.getInn (),
            class_model: this.getClassModel (),
            bank: this.getActiveBank (),
            client_type:        'T',
            additional_comment: 'ТНО'
        }
        return body
    }

    send () {
        const headers = {
            'Content-Type': 'application/json'
        }
        return fetch(this.url, {
            method: 'POST',
            body: JSON.stringify(this.body),
            headers: headers
        }).then(response => {
            return response.json()
        })
    }
}

export default OnStatistic;
