import { setAllButtonFalse } from '@/assets/setButton.js'


class SetClientsModelButton {
    getUserClientModel () {
        return localStorage.getItem('user_class_model')
    }

    setButtonStatus () {
        var button_class = "btn-check header-button-client"
        setAllButtonFalse(button_class)
        let clientModel = this.getUserClientModel()
        if (clientModel == null) {
            document.getElementById(`btn-check-outlined ${this.getUserClientModel()}_model`).checked = true
        }
        document.getElementById(`btn-check-outlined ${this.getUserClientModel()}_model`).checked = true
    }

}

export default SetClientsModelButton;
