class Statistic {
    constructor (event) {
        this.event = event;
        this.id = this.returnRefactorId ()
    }

    returnRefactorId () {
        return this.event.target.id.split(' ')[1]
    }

    getActiveBank () {
        let buttonsCheckbox = document.getElementsByClassName('btn-check header-button')
        for (let bttn of buttonsCheckbox) {
            if (bttn.checked == true) {
                let bttnId = bttn.id.replace('_registry', '').split(' ')[1]
                return bttnId
            }
        }
    }

    getClassModel () {
        let classModel = localStorage.getItem('user_class_model')
        return classModel
    }

    getInn () {
        let inn = document.getElementById(`inn ${this.id}`)
        return inn.value
    }

    getUsername () {
        let username = localStorage.getItem('username')
        console.log(username)
        return username
    }

    getClientType () {
        let clientType = document.getElementById(`client_type ${this.id}`)
        return clientType.value
    }

    getAdditionalComment () {
        let additionslComment = document.getElementById(`additional_comment ${this.id}`)
        console.log(additionslComment.value)
        return additionslComment.value
    }
}


class FetchStatistics {
    constructor (event) {
        this.event = event
        this.body = this.createBody()
        this.url = 'http://127.0.0.1:8000/api/statistics/create_statistic_entry'
    }

    createBody () {
        const StatisticInstance = new Statistic(this.event)
        let body =  {
            username: StatisticInstance.getUsername (),
            inn: StatisticInstance.getInn (),
            class_model: StatisticInstance.getClassModel (),
            bank: StatisticInstance.getActiveBank (),
            client_type: StatisticInstance.getClientType (),
            additional_comment: StatisticInstance.getAdditionalComment ()
        }
        return body
    }

    send () {
        console.log(this.body)
        const headers = {
            'Content-Type': 'application/json'
        }
        return fetch(this.url, {
            method: 'POST',
            body: JSON.stringify(this.body),
            headers: headers
        }).then(response => {
            return response.json()
        })
    }
}


class FetchClient {
    constructor (event) {
        this.event = event
        this.body = this.createBody()
        this.url = 'http://127.0.0.1:8000/api/client/client_update'
    }

    createBody () {
        const StatisticInstance = new Statistic(this.event)
        let body =  {
            username: StatisticInstance.getUsername (),
            inn: StatisticInstance.getInn (),
            name_company: StatisticInstance,
            surname: StatisticInstance,
            first_name: StatisticInstance,
            patronomic: StatisticInstance,
            phone: StatisticInstance,
            adress: StatisticInstance,

            class_model: StatisticInstance,
            bank: StatisticInstance,

            status_inn,
            comment,
            additional_comment,
            send_rko,
            client_type,
            bank_city,
            bank_response,
        }
        return body
    }

    send () {
        console.log(this.body)
        const headers = {
            'Content-Type': 'application/json'
        }
        return fetch(this.url, {
            method: 'POST',
            body: JSON.stringify(this.body),
            headers: headers
        }).then(response => {
            return response.json()
        })
    }
}

export { FetchStatistics, FetchClient };
