class RangeFilterClients {
    constructor (event, store) {
        this.store = store
      this.id = event.target.id
      this.inputId = this.id.replace('-button', '')
      this.inputValue = document.getElementById(this.inputId).value
    }

    filterAray (tableBody) {
        tableBody.slice(0, this.inputValue)
        // for (var client of tableBody) {
        //     console.log(client.id)
        // }
        
    }

    updateStoreTableBody (tableBody) {
        if (this.inputValue) {
            if (this.inputId == 'start-diapason') {
                this.store.commit('updateTableBody', tableBody.slice(0, this.inputValue))
            } else {
                this.store.commit('updateTableBody', tableBody.slice(this.inputValue))
            }
        }
        
        this.filterAray(tableBody)
    }
}

export default RangeFilterClients;
