class OnStatistic {
    constructor (event) {
        this.event = event;
        this.id = event.target.id
        this.url = 'http://127.0.0.1:8000/api/statistics/create_statistic_entry'
        this.body = this.createBody ()
    }

    getActiveBank () {
        let buttonsCheckbox = document.getElementsByClassName('btn-check header-button')
    }

    createBody () {
        let body =  {
            username:           'tanya',
            inn:                '999999999999',
            class_model:        'novoregi',
            bank:               'alfabank',
            client_type:        'T',
            additional_comment: 'ТНО'
        }
        return body
    }

    send () {
        console.log(this.event)
        console.log(this.id)
        const headers = {
            'Content-Type': 'application/json'
        }
        return fetch(this.url, {
            method: 'POST',
            body: JSON.stringify(this.body),
            headers: headers
        }).then(response => {
            return response.json()
        })
    }
}

export default OnStatistic;
